function tvmPV(a,b,c){return a*Math.pow(1+c,-b)}function tvmFV(a,b,c){return a*Math.pow(1+b,c)}function tvmR(a,b,c){return Math.pow(b/a,1/c)-1}function tvmDoubleMoney(a){return Math.log(2)/Math.log(1+a)}function tvmN(a,b,c){return Math.log(b/a)/Math.log(c+1)}function tvmPVoa(a,b,c){return c*((1-Math.pow(1+a,-b))/a)}function tvmFVoa(a,b,c){return c*((Math.pow(1+a,b)-1)/a)}function tvmPVad(a,b,c){return c*((1-Math.pow(1+a,-b))/a)*(1+a)}function tvmFVad(a,b,c){return c*((Math.pow(1+a,b)-1)/a)*(1+a)}function tvmPMToa(a,b,c){return c/((1-Math.pow(1+b,-a))/b)}function tvmPMToaF(a,b,c){return c/((Math.pow(1+b,a)-1)/b)}function tvmPVAoadp(a,b,c,d){return c*((1-Math.pow(1+a,-b))/a)*Math.pow(1+a,-d)}function tvmPVP(a,b){return a/b}function tvmPVPdp(a,b,c){return b*(1/a)*Math.pow(1+a,-c)}function tvmPVPg(a,b,c){return b/(a-c)}function tvmPVPgdp(a,b,c,d){return b*(1/(a-d))*Math.pow(1+a,-c)}function tvmPVmrF(a,b,c,d){return a*Math.pow(1+tvmUtilPeriodicRate(d,c),-tvmUtilPayments(c,b))}function tvmPVmrPMT(a,b,c,d){return d*((1-Math.pow(1+tvmUtilPeriodicRate(c,b),-tvmUtilPayments(b,a)))/tvmUtilPeriodicRate(c,b))}function tvmPMTmrP(a,b,c,d){return a/((1-Math.pow(1+tvmUtilPeriodicRate(d,c),-tvmUtilPayments(c,b)))/tvmUtilPeriodicRate(d,c))}function tvmFVmrP(a,b,c,d){return a*Math.pow(1+tvmUtilPeriodicRate(d,c),tvmUtilPayments(c,b))}function tvmUtilPeriodicRate(a,b){return a/b}function tvmUtilPayments(a,b){return a*b}function tvmUtilEffectiveRate(a,b){return Math.pow(1+tvmUtilPeriodicRate(a,b),b)-1}